package xyz.migoo.mise.framework.core;import xyz.migoo.mise.framework.assertion.AbstractAssertion;/** * @author yacheng.xiao * @date 2019/8/14 16:30 */public class Step {    private String operation;    private String selector;    private String object;    private AbstractAssertion assertion;    public Step(){}    public Step(String operation){        this.operation = operation;    }    public Step(String operation, String selector){        this.operation = operation;        this.selector = selector;    }    public Step(String operation, AbstractAssertion assertion){        this.operation = operation;        this.assertion = assertion;    }    public Step(String operation, String selector, String object){        this.operation = operation;        this.selector = selector;        this.object = object;    }    public Step operation(String operation){        this.operation = operation;        return this;    }    public String operation(){        return operation;    }    public Step selector(String selector){        this.selector = selector;        return this;    }    public String object(){        return object;    }    public Step object(String object){        this.object = object;        return this;    }    public String selector(){        return selector;    }    public Step assertion(AbstractAssertion assertion){        this.assertion = assertion;        return this;    }    public AbstractAssertion assertion(){        return assertion;    }}